export const LIST_BEHAVIOR = {
  name: 'std/List',
  category: 'ui-interaction',
  description: 'Entity list management with CRUD operations',

  dataEntities: [
    {
      name: 'ListState',
      runtime: true,
      singleton: true,
      fields: [
        { name: 'selectedId', type: 'string', default: null },
      ],
    },
  ],

  stateMachine: {
    initial: 'Browsing',
    states: [
      { name: 'Browsing', isInitial: true },
      { name: 'Creating' },
      { name: 'Viewing' },
      { name: 'Editing' },
      { name: 'Deleting' },
    ],
    events: [
      { key: 'INIT' },
      { key: 'CREATE' },
      { key: 'VIEW' },
      { key: 'EDIT' },
      { key: 'DELETE' },
      { key: 'CONFIRM_DELETE' },
      { key: 'CANCEL' },
      { key: 'SAVE' },
    ],
    transitions: [
      {
        from: 'Browsing',
        to: 'Browsing',
        event: 'INIT',
        effects: [
          ['render-ui', 'main', {"type":"page-header","title":"@config.title","actions":[{"label":"Create","event":"CREATE","variant":"primary"}]}],
          ['render-ui', 'main', {"type":"entity-table","entity":"@config.entity","columns":"@config.columns","itemActions":[{"label":"View","event":"VIEW","placement":"row"},{"label":"Edit","event":"EDIT","placement":"row"},{"label":"Delete","event":"DELETE","variant":"danger","placement":"row"}]}],
        ],
      },
      {
        from: 'Browsing',
        to: 'Creating',
        event: 'CREATE',
        effects: [
          ['render-ui', 'modal', {"type":"form-section","entity":"@config.entity","mode":"create","submitEvent":"SAVE","cancelEvent":"CANCEL"}],
        ],
      },
      {
        from: 'Browsing',
        to: 'Viewing',
        event: 'VIEW',
        effects: [
          ['set', '@entity.selectedId', '@payload.id'],
          ['render-ui', 'drawer', {"type":"entity-detail","entity":"@config.entity","id":"@payload.id","actions":[{"label":"Edit","event":"EDIT"},{"label":"Close","event":"CANCEL"}]}],
        ],
      },
      {
        from: 'Browsing',
        to: 'Editing',
        event: 'EDIT',
        effects: [
          ['set', '@entity.selectedId', '@payload.id'],
          ['render-ui', 'drawer', {"type":"form-section","entity":"@config.entity","id":"@payload.id","mode":"edit","submitEvent":"SAVE","cancelEvent":"CANCEL"}],
        ],
      },
      {
        from: 'Viewing',
        to: 'Editing',
        event: 'EDIT',
        effects: [
          ['render-ui', 'drawer', {"type":"form-section","entity":"@config.entity","id":"@entity.selectedId","mode":"edit","submitEvent":"SAVE","cancelEvent":"CANCEL"}],
        ],
      },
      {
        from: 'Browsing',
        to: 'Deleting',
        event: 'DELETE',
        effects: [
          ['set', '@entity.selectedId', '@payload.id'],
          ['render-ui', 'modal', {"type":"confirmation","title":"Delete Confirmation","message":"Are you sure you want to delete this item?","confirmLabel":"Delete","confirmVariant":"danger"}],
        ],
      },
      {
        from: 'Creating',
        to: 'Browsing',
        event: 'SAVE',
        effects: [
          ['persist', 'create', '@config.entity', '@payload.data'],
          ['render-ui', 'modal', null],
          ['notify', {"type":"success","message":"Created successfully"}],
          ['emit', 'INIT'],
        ],
      },
      {
        from: 'Editing',
        to: 'Browsing',
        event: 'SAVE',
        effects: [
          ['persist', 'update', '@config.entity', '@payload.data'],
          ['render-ui', 'drawer', null],
          ['notify', {"type":"success","message":"Updated successfully"}],
          ['emit', 'INIT'],
        ],
      },
      {
        from: 'Deleting',
        to: 'Browsing',
        event: 'CONFIRM_DELETE',
        effects: [
          ['persist', 'delete', '@config.entity', '@entity.selectedId'],
          ['render-ui', 'modal', null],
          ['notify', {"type":"success","message":"Deleted successfully"}],
          ['emit', 'INIT'],
        ],
      },
      {
        from: 'Creating',
        to: 'Browsing',
        event: 'CANCEL',
        effects: [
          ['render-ui', 'modal', null],
        ],
      },
      {
        from: 'Viewing',
        to: 'Browsing',
        event: 'CANCEL',
        effects: [
          ['render-ui', 'drawer', null],
        ],
      },
      {
        from: 'Editing',
        to: 'Browsing',
        event: 'CANCEL',
        effects: [
          ['render-ui', 'drawer', null],
        ],
      },
      {
        from: 'Deleting',
        to: 'Browsing',
        event: 'CANCEL',
        effects: [
          ['render-ui', 'modal', null],
        ],
      },
    ],
  },
};

export const DETAIL_BEHAVIOR = {
  name: 'std/Detail',
  category: 'ui-interaction',
  description: 'Single entity view with edit/delete capabilities',

  dataEntities: [
    {
      name: 'DetailState',
      runtime: true,
      fields: [
        { name: 'entityId', type: 'string', default: null },
        { name: 'isLoading', type: 'boolean', default: false },
        { name: 'hasChanges', type: 'boolean', default: false },
      ],
    },
  ],

  stateMachine: {
    initial: 'Viewing',
    states: [
      { name: 'Viewing', isInitial: true },
      { name: 'Editing' },
      { name: 'Deleting' },
    ],
    events: [
      { key: 'INIT' },
      { key: 'EDIT' },
      { key: 'SAVE' },
      { key: 'CANCEL' },
      { key: 'DELETE' },
      { key: 'CONFIRM_DELETE' },
    ],
    transitions: [
      {
        from: 'Viewing',
        to: 'Viewing',
        event: 'INIT',
        effects: [
          ['render-ui', 'main', {"type":"page-header","title":"@entity.name","actions":[{"label":"Edit","event":"EDIT"},{"label":"Delete","event":"DELETE","variant":"danger"}]}],
          ['render-ui', 'main', {"type":"entity-detail","entity":"@config.entity","fieldNames":"@config.fields"}],
        ],
      },
      {
        from: 'Viewing',
        to: 'Editing',
        event: 'EDIT',
        effects: [
          ['render-ui', 'main', {"type":"form-section","entity":"@config.entity","mode":"edit","fields":"@config.fields","submitEvent":"SAVE","cancelEvent":"CANCEL"}],
        ],
      },
      {
        from: 'Editing',
        to: 'Viewing',
        event: 'SAVE',
        effects: [
          ['persist', 'update', '@config.entity', '@payload.data'],
          ['notify', {"type":"success","message":"Updated successfully"}],
          ['emit', 'INIT'],
        ],
      },
      {
        from: 'Editing',
        to: 'Viewing',
        event: 'CANCEL',
        effects: [
          ['emit', 'INIT'],
        ],
      },
      {
        from: 'Viewing',
        to: 'Deleting',
        event: 'DELETE',
        effects: [
          ['render-ui', 'modal', {"type":"confirmation","title":"Delete Confirmation","message":"Are you sure you want to delete this item?"}],
        ],
      },
      {
        from: 'Deleting',
        to: 'Viewing',
        event: 'CONFIRM_DELETE',
        effects: [
          ['persist', 'delete', '@config.entity', '@entity.id'],
          ['navigate', '@config.returnUrl'],
        ],
      },
      {
        from: 'Deleting',
        to: 'Viewing',
        event: 'CANCEL',
        effects: [
          ['render-ui', 'modal', null],
        ],
      },
    ],
  },
};

export const FORM_BEHAVIOR = {
  name: 'std/Form',
  category: 'ui-interaction',
  description: 'Form state management with validation and submission',

  dataEntities: [
    {
      name: 'FormState',
      runtime: true,
      fields: [
        { name: 'values', type: 'object', default: {} },
        { name: 'errors', type: 'object', default: {} },
        { name: 'touched', type: 'object', default: {} },
        { name: 'isDirty', type: 'boolean', default: false },
        { name: 'isSubmitting', type: 'boolean', default: false },
      ],
    },
  ],

  stateMachine: {
    initial: 'Idle',
    states: [
      { name: 'Idle', isInitial: true },
      { name: 'Editing' },
      { name: 'Validating' },
      { name: 'Submitting' },
      { name: 'Success' },
      { name: 'Error' },
    ],
    events: [
      { key: 'INIT' },
      { key: 'FIELD_CHANGE' },
      { key: 'FIELD_BLUR' },
      { key: 'SUBMIT' },
      { key: 'VALIDATION_PASSED' },
      { key: 'VALIDATION_FAILED' },
      { key: 'SUBMIT_SUCCESS' },
      { key: 'SUBMIT_ERROR' },
      { key: 'RESET' },
    ],
    transitions: [
      {
        from: 'Idle',
        to: 'Editing',
        event: 'INIT',
        effects: [
          ['render-ui', 'main', {"type":"form-section","entity":"@config.entity","fields":"@config.fields","values":"@entity.values","errors":"@entity.errors","submitEvent":"SUBMIT","cancelEvent":"@config.cancelEvent"}],
        ],
      },
      {
        from: 'Editing',
        to: 'Editing',
        event: 'FIELD_CHANGE',
        effects: [
          ['set', '@entity.values', ['object/set', '@entity.values', '@payload.field', '@payload.value']],
          ['set', '@entity.isDirty', true],
        ],
      },
      {
        from: 'Editing',
        to: 'Editing',
        event: 'FIELD_BLUR',
        effects: [
          ['set', '@entity.touched', ['object/set', '@entity.touched', '@payload.field', true]],
        ],
      },
      {
        from: 'Editing',
        to: 'Validating',
        event: 'SUBMIT',
        effects: [
          ['let', [['result', ['validate/check', '@entity.values', '@config.validation']]], ['if', '@result.valid', ['emit', 'VALIDATION_PASSED'], ['do', ['set', '@entity.errors', '@result.errors'], ['emit', 'VALIDATION_FAILED']]]],
        ],
      },
      {
        from: 'Validating',
        to: 'Submitting',
        event: 'VALIDATION_PASSED',
        effects: [
          ['set', '@entity.isSubmitting', true],
          ['if', ['=', '@config.mode', 'create'], ['persist', 'create', '@config.entity', '@entity.values'], ['persist', 'update', '@config.entity', '@entity.values']],
        ],
      },
      {
        from: 'Validating',
        to: 'Editing',
        event: 'VALIDATION_FAILED',
        effects: [
          ['notify', {"type":"error","message":"Please fix the validation errors"}],
        ],
      },
      {
        from: 'Submitting',
        to: 'Success',
        event: 'SUBMIT_SUCCESS',
        effects: [
          ['set', '@entity.isSubmitting', false],
          ['notify', {"type":"success","message":"Saved successfully"}],
          ['emit', '@config.submitEvent', {"data":"@entity.values"}],
        ],
      },
      {
        from: 'Submitting',
        to: 'Error',
        event: 'SUBMIT_ERROR',
        effects: [
          ['set', '@entity.isSubmitting', false],
          ['set', '@entity.errors', {"_form":"@payload.error"}],
          ['notify', {"type":"error","message":"@payload.error"}],
        ],
      },
      {
        from: 'Editing',
        to: 'Idle',
        event: 'RESET',
        effects: [
          ['set', '@entity.values', {}],
          ['set', '@entity.errors', {}],
          ['set', '@entity.touched', {}],
          ['set', '@entity.isDirty', false],
        ],
      },
      {
        from: 'Error',
        to: 'Idle',
        event: 'RESET',
        effects: [
          ['set', '@entity.values', {}],
          ['set', '@entity.errors', {}],
          ['set', '@entity.touched', {}],
          ['set', '@entity.isDirty', false],
        ],
      },
    ],
  },
};

export const MODAL_BEHAVIOR = {
  name: 'std/Modal',
  category: 'ui-interaction',
  description: 'Modal dialog with open/close state management',

  dataEntities: [
    {
      name: 'ModalState',
      runtime: true,
      fields: [
        { name: 'content', type: 'object', default: null },
      ],
    },
  ],

  stateMachine: {
    initial: 'Closed',
    states: [
      { name: 'Closed', isInitial: true },
      { name: 'Open' },
    ],
    events: [
      { key: 'OPEN' },
      { key: 'CLOSE' },
      { key: 'CONFIRM' },
    ],
    transitions: [
      {
        from: 'Closed',
        to: 'Open',
        event: 'OPEN',
        effects: [
          ['set', '@entity.content', '@payload.content'],
          ['render-ui', 'modal', {"type":"@payload.type","onClose":"CLOSE"}],
        ],
      },
      {
        from: 'Open',
        to: 'Closed',
        event: 'CLOSE',
        effects: [
          ['render-ui', 'modal', null],
        ],
      },
      {
        from: 'Open',
        to: 'Closed',
        event: 'CONFIRM',
        effects: [
          ['render-ui', 'modal', null],
        ],
      },
    ],
  },
};

export const DRAWER_BEHAVIOR = {
  name: 'std/Drawer',
  category: 'ui-interaction',
  description: 'Side drawer panel for detail views and forms',

  dataEntities: [
    {
      name: 'DrawerState',
      runtime: true,
      fields: [
        { name: 'content', type: 'object', default: null },
      ],
    },
  ],

  stateMachine: {
    initial: 'Closed',
