    events: [
      { key: 'OPEN' },
      { key: 'CLOSE' },
    ],
    transitions: [
      {
        from: 'Closed',
        to: 'Open',
        event: 'OPEN',
        effects: [
          ['set', '@entity.content', '@payload.content'],
