export const TABS_BEHAVIOR = {
  name: 'std/Tabs',
  category: 'ui-interaction',
  description: 'Tabbed navigation within a page',

  dataEntities: [
    {
      name: 'TabsState',
      runtime: true,
      singleton: true,
      fields: [
        { name: 'activeTab', type: 'string', default: null },
      ],
    },
  ],

  stateMachine: {
    initial: 'Active',
    states: [
      { name: 'Active', isInitial: true },
    ],
    events: [
      { key: 'INIT' },
      { key: 'SELECT_TAB' },
    ],
    transitions: [
      {
        from: 'Active',
        to: 'Active',
        event: 'INIT',
        effects: [
          ['set', '@entity.activeTab', '@config.defaultTab'],
          ['render-ui', 'main', {"type":"filter-group","filterType":"tabs","tabs":"@config.tabs","active":"@entity.activeTab","onSelect":"SELECT_TAB"}],
        ],
      },
      {
        from: 'Active',
        to: 'Active',
        event: 'SELECT_TAB',
        effects: [
          ['set', '@entity.activeTab', '@payload.tabId'],
        ],
      },
    ],
  },
};

export const WIZARD_BEHAVIOR = {
  name: 'std/Wizard',
  category: 'ui-interaction',
  description: 'Multi-step wizard flow - each step is a state',

  dataEntities: [
    {
      name: 'WizardState',
      runtime: true,
      singleton: true,
      fields: [
        { name: 'stepData', type: 'object', default: {} },
      ],
    },
  ],

  stateMachine: {
    initial: 'Step1',
    states: [
      { name: 'Step1', isInitial: true },
      { name: 'Step2' },
      { name: 'Step3' },
      { name: 'Complete' },
    ],
    events: [
      { key: 'INIT' },
      { key: 'NEXT' },
      { key: 'PREV' },
      { key: 'COMPLETE' },
    ],
    transitions: [
      {
        from: 'Step1',
        to: 'Step1',
        event: 'INIT',
        effects: [
          ['render-ui', 'main', {"type":"wizard-progress","steps":["Step 1","Step 2","Step 3"],"current":0}],
          ['render-ui', 'main', {"type":"form-section","entity":"@config.entity","fields":"@config.step1Fields","submitEvent":"NEXT"}],
        ],
      },
      {
        from: 'Step1',
        to: 'Step2',
        event: 'NEXT',
        effects: [
          ['set', '@entity.stepData.step1', '@payload'],
          ['render-ui', 'main', {"type":"wizard-progress","steps":["Step 1","Step 2","Step 3"],"current":1}],
          ['render-ui', 'main', {"type":"form-section","entity":"@config.entity","fields":"@config.step2Fields","submitEvent":"NEXT","cancelEvent":"PREV"}],
        ],
      },
      {
        from: 'Step2',
        to: 'Step1',
        event: 'PREV',
        effects: [
          ['emit', 'INIT'],
        ],
      },
      {
        from: 'Step2',
        to: 'Step3',
        event: 'NEXT',
        effects: [
          ['set', '@entity.stepData.step2', '@payload'],
          ['render-ui', 'main', {"type":"wizard-progress","steps":["Step 1","Step 2","Step 3"],"current":2}],
          ['render-ui', 'main', {"type":"entity-detail","entity":"@config.entity","fieldNames":["step1","step2"],"title":"Review"}],
          ['render-ui', 'main', {"type":"form-section","submitLabel":"Complete","cancelLabel":"Back","submitEvent":"COMPLETE","cancelEvent":"PREV"}],
        ],
      },
      {
        from: 'Step3',
        to: 'Step2',
        event: 'PREV',
        effects: [
          ['render-ui', 'main', {"type":"wizard-progress","steps":["Step 1","Step 2","Step 3"],"current":1}],
          ['render-ui', 'main', {"type":"form-section","entity":"@config.entity","fields":"@config.step2Fields","submitEvent":"NEXT","cancelEvent":"PREV"}],
        ],
      },
      {
        from: 'Step3',
        to: 'Complete',
        event: 'COMPLETE',
        effects: [
          ['persist', 'create', '@config.entity', '@entity.stepData'],
          ['notify', {"type":"success","message":"Wizard completed!"}],
          ['navigate', '@config.completionUrl'],
        ],
      },
    ],
  },
};

export const MASTERDETAIL_BEHAVIOR = {
  name: 'std/MasterDetail',
  category: 'ui-interaction',
  description: 'Master-detail layout with synchronized list and detail views',

  dataEntities: [
    {
      name: 'MasterDetailState',
      runtime: true,
      singleton: true,
      fields: [
        { name: 'selectedId', type: 'string', default: null },
      ],
    },
  ],

  stateMachine: {
    initial: 'NoSelection',
    states: [
      { name: 'NoSelection', isInitial: true },
      { name: 'Selected' },
    ],
    events: [
      { key: 'INIT' },
      { key: 'SELECT' },
      { key: 'DESELECT' },
    ],
    transitions: [
      {
        from: 'NoSelection',
        to: 'NoSelection',
        event: 'INIT',
        effects: [
          ['render-ui', 'main', {"type":"master-detail","entity":"@config.entity","masterColumns":"@config.masterColumns","onSelect":"SELECT","selected":"@entity.selectedId"}],
          ['render-ui', 'main', {"type":"empty-state","message":"Select an item to view details"}],
        ],
      },
      {
        from: 'NoSelection',
        to: 'Selected',
        event: 'SELECT',
        effects: [
          ['set', '@entity.selectedId', '@payload.id'],
          ['render-ui', 'main', {"type":"entity-detail","entity":"@config.entity","id":"@payload.id","fieldNames":"@config.detailFields"}],
        ],
      },
      {
        from: 'Selected',
        to: 'Selected',
        event: 'SELECT',
        effects: [
          ['set', '@entity.selectedId', '@payload.id'],
          ['render-ui', 'main', {"type":"entity-detail","entity":"@config.entity","id":"@payload.id","fieldNames":"@config.detailFields"}],
        ],
      },
      {
        from: 'Selected',
        to: 'NoSelection',
        event: 'DESELECT',
        effects: [
          ['set', '@entity.selectedId', null],
          ['render-ui', 'main', {"type":"empty-state","message":"Select an item to view details"}],
        ],
      },
    ],
  },
};

export const FILTER_BEHAVIOR = {
  name: 'std/Filter',
  category: 'ui-interaction',
  description: 'Filter and search management for lists',

  dataEntities: [
    {
      name: 'FilterState',
      runtime: true,
      singleton: true,
      fields: [
        { name: 'filters', type: 'object', default: {} },
        { name: 'searchTerm', type: 'string', default: "" },
      ],
    },
  ],

  stateMachine: {
    initial: 'Idle',
    states: [
      { name: 'Idle', isInitial: true },
      { name: 'Filtering' },
    ],
    events: [
      { key: 'INIT' },
      { key: 'SET_FILTER' },
      { key: 'CLEAR_FILTERS' },
      { key: 'SEARCH' },
    ],
    transitions: [
      {
        from: 'Idle',
        to: 'Idle',
        event: 'INIT',
        effects: [
          ['render-ui', 'main', {"type":"filter-group","filters":"@config.filters","values":"@entity.filters","onFilterChange":"SET_FILTER","onClear":"CLEAR_FILTERS"}],
        ],
      },
      {
        from: 'Idle',
        to: 'Filtering',
        event: 'SET_FILTER',
        effects: [
          ['set', '@entity.filters', ['object/set', '@entity.filters', '@payload.field', '@payload.value']],
          ['emit', 'FILTER_CHANGED', '@entity.filters'],
        ],
      },
      {
        from: 'Filtering',
        to: 'Idle',
        event: 'SET_FILTER',
        effects: [
          ['set', '@entity.filters', ['object/set', '@entity.filters', '@payload.field', '@payload.value']],
          ['emit', 'FILTER_CHANGED', '@entity.filters'],
        ],
      },
      {
        from: 'Filtering',
        to: 'Idle',
        event: 'CLEAR_FILTERS',
        effects: [
          ['set', '@entity.filters', {}],
          ['set', '@entity.searchTerm', ''],
          ['emit', 'FILTER_CHANGED', {}],
        ],
      },
      {
        from: 'Idle',
        to: 'Filtering',
        event: 'SEARCH',
        effects: [
          ['set', '@entity.searchTerm', '@payload.term'],
          ['emit', 'SEARCH_CHANGED', '@payload.term'],
        ],
      },
    ],
  },
};

export const PAGINATION_BEHAVIOR = {
  name: 'std/Pagination',
  category: 'data-management',
  description: 'Page-based navigation for large data sets',

  dataEntities: [
    {
      name: 'PaginationState',
      runtime: true,
      singleton: true,
      fields: [
        { name: 'page', type: 'number', default: 1 },
        { name: 'pageSize', type: 'number', default: 20 },
        { name: 'totalItems', type: 'number', default: 0 },
      ],
    },
  ],

  stateMachine: {
    initial: 'Active',
    states: [
      { name: 'Active', isInitial: true },
    ],
    events: [
      { key: 'INIT' },
      { key: 'NEXT_PAGE' },
      { key: 'PREV_PAGE' },
      { key: 'GO_TO_PAGE' },
      { key: 'SET_PAGE_SIZE' },
    ],
    transitions: [
      {
        from: '*',
        event: 'INIT',
        effects: [
          ['set', '@entity.page', 1],
          ['set', '@entity.pageSize', '@config.defaultPageSize'],
        ],
      },
      {
        event: 'NEXT_PAGE',
        guard: ['<', '@entity.page', ['math/ceil', ['/', '@entity.totalItems', '@entity.pageSize']]],
        effects: [
          ['set', '@entity.page', ['+', '@entity.page', 1]],
        ],
      },
      {
        event: 'PREV_PAGE',
        guard: ['>', '@entity.page', 1],
        effects: [
          ['set', '@entity.page', ['-', '@entity.page', 1]],
        ],
      },
      {
        event: 'GO_TO_PAGE',
        guard: ['and', ['>=', '@payload.page', 1], ['<=', '@payload.page', ['math/ceil', ['/', '@entity.totalItems', '@entity.pageSize']]]],
        effects: [
          ['set', '@entity.page', '@payload.page'],
        ],
      },
      {
        event: 'SET_PAGE_SIZE',
        effects: [
          ['set', '@entity.pageSize', '@payload.size'],
          ['set', '@entity.page', 1],
        ],
      },
    ],
  },
};

export const SELECTION_BEHAVIOR = {
  name: 'std/Selection',
  category: 'data-management',
  description: 'Single or multi-selection management',

  dataEntities: [
    {
      name: 'SelectionState',
      runtime: true,
      singleton: true,
      fields: [
        { name: 'selected', type: 'array', default: [] },
